mapper.select:
SELECT
COUNT(id)
FROM jsh_material
WHERE 1=1

  and name like '%${name}%'


  and company_sku=#{companySku}


  and fnsku=#{fnsku}


  and brand=#{brand}


  and category_id=#{categoryId}

and ifnull(delete_flag,'0') !='1'

select * FROM jsh_material where 1=1

  and name like '%${name}%'


  and company_sku=#{companySku}


  and fnsku=#{fnsku}


  and brand=#{brand}


  and category_id=#{categoryId}

and ifnull(delete_flag,'0') !='1'
order by id desc

  limit #{offset},#{rows}
