mapper.select:
select * FROM jsh_sample where 1=1

  and goods_name like '%${name}%'


  and brand=#{brand}

and ifnull(delete_flag,'0') !='1'
order by id desc

  limit #{offset},#{rows}

mapper.update:
update jsh_sample set delete_flag ='1' where 1=1 and ifnull(delete_flag,'0') !='1' and id in (
#{id}
)
